      2
      */
		point3 target = rec.p + rec.normal + random_unit_vector(); 
		return 0.5 * ray_color(ray(rec.p, target - rec.p), world, depth - 1); 
	}
	// 
	vec3 unit_direction = unit_vector(r.direction());
